import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import LocalLibraryRoundedIcon from '@material-ui/icons/LocalLibraryRounded';import AccountCircleIcon from '@material-ui/icons/AccountCircle';
const useStyles = makeStyles((theme) => ({
  title: {
    flexGrow:1,
    textAlign:'left',
  },
  AppBar: {
    backgroundColor:'black',
  },
  Button: {
    backgroundColor:'rgba(255,255,255,.2)',
    flexGrow:0,
    
  }
}));

export default function ButtonAppBar() {
  const classes = useStyles();

  return (
    <div>
      <AppBar position="static" className={classes.AppBar}>
        <Toolbar>
          <Typography variant="h5" className={classes.title}>
            <LocalLibraryRoundedIcon/> e-Library
          </Typography>
          <Button color="inherit" className={classes.Button}>
          <Route>
            <Link to='/signin'><AccountCircleIcon/> Login</Link>
            <Switch>
          <Route path="/signin">
          </Route>
          </Switch>
          </Route>
          </Button>
        </Toolbar>
      </AppBar>
    </div>
  );
}
